#!/bin/bash

echo "=== Testing Face Authentication Flow ==="
echo ""

# Test 1: AI Backend Status
echo "1. Checking AI Backend Status..."
AI_STATUS=$(curl -s http://127.0.0.1:5002/api/face/status)
echo "   Response: $AI_STATUS"
echo ""

# Test 2: Test verify-frame with mock data
echo "2. Testing verify-frame endpoint..."
VERIFY_RESPONSE=$(curl -s -X POST http://127.0.0.1:5002/api/face/verify-frame \
  -H "Content-Type: application/json" \
  -d '{"image":"data:image/jpeg;base64,"}')
echo "   Response: $VERIFY_RESPONSE"
echo ""

# Test 3: Test auth service face login with username
echo "3. Testing auth service face login (with username)..."
AUTH_RESPONSE=$(curl -s -X POST http://localhost:3001/api/v1/auth/login-face \
  -H "Content-Type: application/json" \
  -d '{"imageData":"data:image/jpeg;base64,","username":"medazizkammoun"}')
echo "   Response: $AUTH_RESPONSE"
echo ""

# Test 4: Check if auth service can reach AI backend
echo "4. Testing if auth service can reach AI backend..."
AUTH_TO_AI=$(curl -s http://localhost:3001/api/v1/auth/test-ai 2>&1 || echo "No test endpoint")
echo "   Response: $AUTH_TO_AI"
echo ""

echo "=== Test Complete ==="
